let output = {};

output.project_id = globals.project_id;

output.id = properties['Todoist ID'].rich_text.length > 0 ? 
  properties['Todoist ID'].rich_text[0].text.content : 
  null;

output.content = properties['Name'].title.length > 0 ? properties['Name'].title[0].text.content : '[empty]';

output.description = "[↗ Open in Notion](" + $('Get Notion task').item.json.url + ")"

// Map priority
if (properties['Priority'].select === null) {
  output.priority = "1"; // P4
} else {
  output.priority = {
    "do first": "4", // P1
    "urgent": "3", // P2
    "important": "2" // P3
  }[properties['Priority'].select.name] ?? "1"; // P4
}

// Map section
const statusName = properties['Status'].status.name;
output.section_id = null;
if (!['Done', 'Obsolete'].includes(statusName)) {
  const sectionMap = Object.fromEntries(
      globals.sections.map(section => [section.name, section.id])
  );
  if (!sectionMap.hasOwnProperty(statusName)) {
      throw new Error("No Todoist section found for status '" + statusName + "'");
  }
  output.section_id = sectionMap[statusName];
}

// Map completed by status "Done"
output.is_completed = false;
if (properties['Status'].status.name == "Done") {
  output.is_completed = true;
}

// Set UTC if time is set
if (properties['Due'].date !== null) {
  output.due_datetime = properties.Due.date.start
  if (properties.Due.date.start.length > 10) {
    output.due_datetime = new Date(properties.Due.date.start).toISOString();
  }
} else {
  // API requires due_string instead of due_datetime when date/time is removed
  output.due_string = "no date";
}

return { json: output };

// Get the markdown from the input
const markdown = $('Get Todoist Task').first().json.description || '';

// Split the markdown into lines and remove empty lines
const lines = markdown.split('\n').filter(line => line.trim());

// Initialize result array
const result = [];

// Helper function to split long text into chunks
function splitIntoChunks(text, maxLength) {
    const chunks = [];
    let remaining = text;

    while (remaining.length > maxLength) {
        // Find the last space before maxLength
        let splitIndex = remaining.lastIndexOf(' ', maxLength);
        // If no space found, force split at maxLength
        if (splitIndex === -1) splitIndex = maxLength;

        chunks.push(remaining.substring(0, splitIndex).trim());
        remaining = remaining.substring(splitIndex).trim();
    }

    if (remaining.length > 0) {
        chunks.push(remaining);
    }

    return chunks;
}

for (const line of lines) {
    const trimmedLine = line.trim();

    // Skip if empty line
    if (!trimmedLine) continue;

    // Check for different types of markdown elements
    if (trimmedLine.startsWith('# ')) {
        result.push({
            type: 'heading_1',
            content: trimmedLine.substring(2).trim()
        });
    }
    else if (trimmedLine.startsWith('## ')) {
        result.push({
            type: 'heading_2',
            content: trimmedLine.substring(3).trim()
        });
    }
    else if (trimmedLine.startsWith('### ')) {
        result.push({
            type: 'heading_3',
            content: trimmedLine.substring(4).trim()
        });
    }
    else if (trimmedLine.match(/^\d+\./)) {
        result.push({
            type: 'numbered_list_item',
            content: trimmedLine.replace(/^\d+\.\s*/, '').trim()
        });
    }
    else if (trimmedLine.startsWith('- ')) {
        result.push({
            type: 'bulleted_list_item',
            content: trimmedLine.substring(2).trim()
        });
    }
    else {
        // For paragraphs, check if they need to be split
        if (trimmedLine.length > 2000) {
            const chunks = splitIntoChunks(trimmedLine, 2000);
            for (const chunk of chunks) {
                result.push({
                    type: 'paragraph',
                    content: chunk
                });
            }
        } else {
            result.push({
                type: 'paragraph',
                content: trimmedLine
            });
        }
    }
}

// Return the result in the format n8n expects
return { blocks: result };
